function myFunction(){var e=document.getElementById("previous-positions"),t=document.getElementById("toggle-previous-positions");e.style.display="block",t.style.display="none"}window.addEventListener("DOMContentLoaded",()=>{const e=new IntersectionObserver(e=>{e.forEach(e=>{const t=e.target.getAttribute("id");e.intersectionRatio>0?document.querySelector(`nav li a[href="#${t}"]`).parentElement.classList.add("active"):document.querySelector(`nav li a[href="#${t}"]`).parentElement.classList.remove("active")})});document.querySelectorAll("h2[id], h3[id]").forEach(t=>{e.observe(t)})})